// Updated Gauge Configuration for Muscle Stimulator
var muscleStimGauge = new Gauge(document.getElementById("frequency-gauge")).setOptions({
  minValue: 2,
  maxValue: 150,
  angle: 0.15,
  lineWidth: 0.2,
  radiusScale: 1.0,
  pointer: {
    length: 0.6,
    strokeWidth: 0.035,
    color: '#000'
  },
  staticZones: [
    {strokeStyle: "#30B32D", min: 2, max: 60},    // Green - Normal range
    {strokeStyle: "#FFDD00", min: 60, max: 100},  // Yellow - Medium intensity
    {strokeStyle: "#F03E3E", min: 100, max: 150}  // Red - Extreme intensity
  ],
  renderTicks: {
    divisions: 5,
    divWidth: 1.5,
    divLength: 0.7,
    divColor: "#333",
    subDivisions: 4,
    subLength: 0.5,
    subWidth: 0.6,
    subColor: "#666"
  },
  limitMin: true,
  limitMax: true,
  fontSize: 24,
  colorStart: "#6f6ea0", // Not used when staticZones is defined
  colorStop: "#c0c0db",  // Not used when staticZones is defined
  strokeColor: "#f0f0f0"  // Background color
});

// Set initial value
muscleStimGauge.set(20); // Default starting frequency